package day13forloop;

public class forloopeg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// This is the example pgm for for loop
		//for loop does everything as while but in for loop te actions takes place in singlke line
		// initialization,condition and increment
		int i=1;
		for(i=1;i<=3;i++)// incremental way
		{
			System.out.println("Example for for loop");
		}
		// the above loop displays the op three times.
		for(int b=5;b>=1;b--)
		{
			System.out.println("This is a decremental way of executing the loop");// this loop is a decremntal way of executing the loop
		}
	}

}
