package day8Controlifandoperators;

public class Logicoprtr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean c=true;
		boolean d=false;
		boolean e=true;
		boolean f=false;
		System.out.println("Pgm for Logical Oprt");
		System.out.println(" LOGICAL AND EXAMPLE");
		System.out.println(c&&d);
		System.out.println(d&&e);
		System.out.println(e&&f);
		System.out.println(" LOGICAL OR EXAMPLE");
		System.out.println(c||d);
		System.out.println(d||e);
		System.out.println(e||f);
		System.out.println(" LOGICAL NOT EXAMPLE");
		System.out.println(!c);
		System.out.println(!e);
		System.out.println(!f);
		
		
		

	}

}
